    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block body %}
    <div class=\"container\">
        <div class=\"d-flex flex-column form-login align-items-center justify-content-center\">
            <h1 class=\"h3 mb-3 font-weight-normal d-block text-white\">Connexion</h1>
            <form method=\"post\">
                {% if error %}
                    <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                {% endif %}
                <div class=\"form-row\">
                    <div class=\"col\">
                        <label for=\"inputLogin\">Login</label>
                        <input type=\"text\" value=\"{{ last_username }}\" name=\"login\" id=\"inputLogin\" class=\"form-control form-control p-lg-5 input-login\" required autofocus>
                    </div>
                    <div class=\"col\">

                        <label for=\"inputPassword\">Password</label>
                        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control form-control p-lg-5 input-login\" required>
                    </div>
                </div>
                <input type=\"hidden\" name=\"_csrf_token\"
                       value=\"{{ csrf_token('authenticate') }}\"
                >
                {#
                    Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
                    See https://symfony.com/doc/current/security/remember_me.html
                    <div class=\"checkbox mb-3\">
                        <label>
                            <input type=\"checkbox\" name=\"_remember_me\"> Remember me
                        </label>
                    </div>
                #}
                <button class=\"btn btn-dark btn-lg w-100 btn-connexion p-lg-3\" type=\"submit\">
                    Sign in
                </button>
            </form>
        </div>
    </div>

{% endblock %}", "security/login.html.twig", "/opt/lampp/htdocs/symfony/Sama_Chambre/templates/security/login.html.twig");
    }
